mapper.update:
update member
set
	passwd = #{passwd}
where mid = #{mid}

mapper.select:
select m.*, t.teamname, g.gradename 
from member m left join team t on (m.tcode = t.teamcode)
left join grade g on(m.gcode = g.gradecode)
WHERE mid = #{mid}


select m.*, t.teamname, g.gradename 
from member m left join team t on (m.tcode = t.teamcode)
left join grade g on(m.gcode = g.gradecode)
where removeyn = 'n'


limit #{startNum} , #{perPage}
